import { BsChatLeftTextFill,BsWhatsapp,BsPersonCircle,BsBroadcastPin,BsFillPlayFill,BsFillBellFill} from "react-icons/bs";
import './App.css';
import { FaUserAlt } from "react-icons/fa";
import {BrowserRouter as Router,Routes,Route, Link} from 'react-router-dom'
import Inbox from "./Components/Inbox";
import { useNavigate } from "react-router-dom";


function App() {
  const naviage = useNavigate()
  return (
    <>
      <nav>
        <div className="logo">
          <BsWhatsapp className="logo-img" />
        </div>
        <div className="list">

          <p className="para1" onClick={()=>nav}><BsChatLeftTextFill/> INBOX</p>
          <p className="para1"><BsPersonCircle/> CONTACTS</p>
          <p className="para1"><BsBroadcastPin/> INBOX</p>
          <p className="para1"><BsFillPlayFill/> BROADCAST</p>

        </div>
        <div className="right-sec">
          <div className="bell">
          <BsFillBellFill className="bell-icon"/>
          </div>
          <div className="conn">
            <p className="text1">Connected</p>
            <p className="num">+91 999999999</p>
          </div>
          <div>
            <FaUserAlt className="user-icon"/>
            <p className="user-text">User</p>
          </div>

        </div>


      </nav>

      <Inbox/>
    </>
  );
}

export default App;
